mapper.insert:
INSERT INTO board (

) VALUES (
#{bNo}
, #{bTitle}
, #{bContent}
, #{bWriter}
, 0
, #{bNotice_yn}
, 0
, NOW()
, NULL
)

mapper.update:
UPDATE board
SET
bModified_date = NOW()
, bTitle = #{bTitle}
, bContent = #{bContent}
, bWriter = #{bWriter}
, bNotice_yn = #{bNotice_yn}
WHERE
bNo = #{bNo}

mapper.delete:
UPDATE board
SET bDelete_yn = 1
WHERE bNo = #{bNo}
